'use strict'

const R = require('ramda')
const gx = require('./gx-stream')

function* makeGenerator() {
    yield 'fred'
    yield 'george'
    yield 'waldo'
}

let stream = gx.of(makeGenerator())
let transformedStream = stream.map(R.toUpper).map(R.concat('Mr. '))
// Mr. FRED
console.log(transformedStream.next())
console.log(transformedStream.take(1))

let waldoStream = gx.of(makeGenerator())
    .map(R.toUpper)
    .map(R.concat('Mr. '))
    .filter(R.equals('Mr. WALDO'))

console.log(waldoStream.take(1))
